    _wrapperGetMessageCallbacks(successCB, failCB) {
        let cbs = [
            protoMsgsStr => {
                var protoMsgs = JSON.parse(protoMsgsStr);
                let msgs = [];
                protoMsgs.map(m => {
                    let msg = Message.fromProtoMessage(m);
                    if (msg) {
                        msgs.push(msg);
                    }
                });
                successCB && successCB(msgs);
            },
            err => {
                failCB && failCB(err);
            }
        ]
        return cbs;
    }

